package loop;

public class Basic_increment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//5---->10---------------->15---------->20
		int a=5;
		System.out.println(a);//5
		//increment------->+
		//decrement-------------->-
		//a=a+1;------------->a++
		a++;//6
		System.out.println(a);//6
		
		
		a=a+3;//9
		System.out.println(a);
		a--;//a=a-1
		System.out.println(a);
		a=a-3;
		System.out.println(a);}
}

