package loop;

public class For_exp5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//pre and post increment
		{
			int a =10;
			System.out.println(a);
			int i;
			//post increment 
			i=a++;
			
			System.out.println(i);
			System.out.println(a);
		}

	}

}
