package loop;

public class Post_increment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int z=99;
int y;
System.out.println(z);

y=z++; //post increment
System.out.println(y);
System.out.println(z++);
System.out.println(z);
//pre increment
//int m=99;
//int n;
	}

}
